// ---------------------------------------------------------------
// Process: SC_METHOD wait_for_end
// Static sensitivity: sensitive << clk.pos() << testbench_end_event;

void mc_testbench::wait_for_end() {
   // If run() has not finished, we do nothing here
   if (!testbench_ended) return;
   // check for completed outputs
   if (out_comp->get_compare_count() < out_capture_count) {testbench_end_event.notify(1,SC_NS); return;}
   // If we made it here, all outputs have flushed. Check the results
   SC_REPORT_INFO(name(), "Simulation completed");
   check_results();
   sc_stop();
}
// ---------------------------------------------------------------
// Process: SC_THREAD run
// Static sensitivity: 

void mc_testbench::run() {
   testbench::enable_idle_sync_mode = false;
   testbench::idle_sync_stable_cycles = 1;
   _channel_mismatch = false;
   testbench::image_in_ignore = false;
   testbench::image_in_skip = false;
   testbench::image_in_skip_quiet = false;
   testbench::image_in_skip_once = false;
   testbench::image_in_skip_noerr = false;
   testbench::image_in_array_comp_first = -1;
   testbench::image_in_array_comp_last = -1;
   testbench::image_in_wait_ctrl.clear();
   image_in_capture_count = 0;
   image_in_iteration_count = 0;
   image_in_access_ptr = 0;
   testbench::out_ignore = false;
   testbench::out_skip = false;
   testbench::out_skip_quiet = false;
   testbench::out_skip_once = false;
   testbench::out_skip_noerr = false;
   testbench::out_array_comp_first = -1;
   testbench::out_array_comp_last = -1;
   testbench::out_use_mask = false;
   testbench::out_output_mask = (ac_fixed<32, 16, true, AC_TRN, AC_WRAP >) ~0;
   testbench::out_wait_ctrl.clear();
   out_capture_count = 0;
   out_iteration_count = 0;
   out_access_ptr = 0;
   testbench testbench_inst(sc_argc(), sc_argv());
   main_exit_code = testbench_inst.main();
   cout<<"Info: Execution of user-supplied C++ testbench 'main()' has completed with exit code = " << main_exit_code << endl;
   cout<<endl;
   cout<<"Info: Collecting data completed"<<endl;
   cout<<"   captured "<<image_in_capture_count<<" values of image_in"<<endl;
   cout<<"   captured "<<out_capture_count<<" values of out"<<endl;
   testbench_ended = true;
   testbench_end_event.notify(SC_ZERO_TIME);
}
